class Solution:
    def countNonDecreasingSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        dp = [0] * (n + 1)
        dp[0] = 1
        for i in range(1, n + 1):
            dp[i] = dp[i - 1]
            for j in range(i):
                if nums[i - 1] >= nums[j]:
                    dp[i] += dp[j]
                else:
                    dp[i] += dp[j] - (nums[i - 1] - nums[j] - 1) // k
        return dp[n]
