class Solution:
    def countNonDecreasingSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        prefix_sum = [0] * (n + 1)
        for i in range(n):
            prefix_sum[i + 1] = prefix_sum[i] + nums[i]

        def count_subarrays(start, end):
            return (end - start) * (end - start + 1) // 2

        def binary_search(target):
            left, right = 0, n
            while left < right:
                mid = (left + right) // 2
                if count_subarrays(0, mid) <= target:
                    left = mid + 1
                else:
                    right = mid
            return left

        ans = 0
        for i in range(n):
            left = binary_search(k - (prefix_sum[i + 1] - prefix_sum[i]))
            ans += count_subarrays(0, left)
        return ans
