class Solution:
    def maximumAmount(self, coins: List[List[int]]) -> int:
        m, n = len(coins), len(coins[0])
        dp = [[0] * n for _ in range(m)]

        # Initialize the first row and first column
        for i in range(m):
            dp[i][0] = coins[i][0]
        for j in range(n):
            dp[0][j] = coins[0][j]

        # Fill the dp table
        for i in range(1, m):
            for j in range(1, n):
                dp[i][j] = max(dp[i-1][j], dp[i][j-1]) + coins[i][j]

        return dp[m-1][n-1]
