class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        graph = defaultdict(list)
        for u, v, length in edges:
            graph[u].append((v, length))
            graph[v].append((u, length))

        def dfs(node, parent):
            nonlocal max_length, min_length
            left_length, right_length = 0, 0
            for child, length in graph[node]:
                if child != parent:
                    child_length, child_min_length = dfs(child, node)
                    if nums[node] != nums[child]:
                        if child_length > left_length:
                            left_length, right_length = child_length, left_length
                        elif child_length > right_length:
                            right_length = child_length
                    min_length = min(min_length, child_min_length)
            if left_length == 0 and right_length == 0:
                max_length = max(max_length, 0)
            elif left_length == 0:
                max_length = max(max_length, right_length + 1)
            elif right_length == 0:
                max_length = max(max_length, left_length + 1)
            else:
