            if i == len(s) or j == len(p):
                return i == len(s) and j == len(p)
            if s[i] == p[j]:
                return is_valid(i + 1, j + 1)
            if p[j] == '*':
                return is_valid(i + 1, j) or is_valid(i, j + 1)
            return False

        def find_shortest(i, j):
            if i == len(s) or j == len(p):
                return len(s) - i
            if s[i] == p[j]:
                return find_shortest(i + 1, j + 1)
            if p[j] == '*':
                return min(find_shortest(i + 1, j), find_shortest(i, j + 1))
            return float('inf')

        for i in range(len(s)):
            for j in range(len(p)):
                if s[i] == p[j]:
                    if is_valid(i, j):
                        return find_shortest(i, j)
        return -1
