                # If the '*' matches one or more characters, move the string pointer forward
                else:
                    i += 1
            # If the current character in the pattern is not a '*', move the pattern pointer forward
            else:
                j += 1

        # If the pattern pointer has reached the end of the pattern, return the length of the string
        if j == m:
            min_len = min(min_len, n - i)
        # If the string pointer has reached the end of the string, return -1
        else:
            min_len = -1

        return min_len
