class Solution:
    def minCostGoodCaption(self, caption: str) -> str:
        n = len(caption)
        if n < 3:
            return caption

        # Create a list to store the minimum cost to make each character good
        dp = [0] * 26

        # Iterate over the caption
        for i in range(n):
            # Calculate the cost to make the current character good
            cost = 0
            if caption[i] == 'a':
                cost = 1
            elif caption[i] == 'z':
                cost = 1
            else:
                cost = 2

            # Update the minimum cost for each character
            for j in range(26):
                if j == ord(caption[i]) - ord('a'):
                    dp[j] = min(dp[j], cost)
                elif j == ord(caption[i]) - ord('a') + 1:
                    dp[j] = min(dp[j], cost + 1)
                elif j == ord(caption[i]) - ord('a') - 1:
                    dp[j] = min(dp[j], cost + 1)

        # Find the minimum cost to make the entire caption good
        min_cost = float('inf')
        for i in range(26):
            min_cost = min(min_cost, dp[i])

        # If the minimum cost is greater than 2, it's impossible to make the caption good
        if min_cost > 2:
            return ""

        # Construct the good caption
        good_caption = ""
        for i in range(n):
            if caption[i] == 'a':
                good_caption += 'a'
            elif caption[i] == 'z':
                good_caption += 'z'
            else:
                good_caption += chr(ord(caption[i]) - min_cost + ord('a'))

        return good_caption
