            else:
                # Update the minimum cost to make the current character good
                dp[caption[i] - 'a'] = min(dp[caption[i] - 'a'], dp[caption[i-1] - 'a'] + 1)

        # Create a list to store the result
        result = []

        # Iterate over the caption from left to right
        for i in range(n):
            # Check if the current character is not 'a' or 'z'
            if caption[i] != 'a' and caption[i] != 'z':
                # Check if the previous character is not 'a' or 'z'
                if caption[i-1] != 'a' and caption[i-1] != 'z':
                    # Check if the next character is not 'a' or 'z'
                    if caption[i+1] != 'a' and caption[i+1] != 'z':
