class Solution:
    def minCostGoodCaption(self, caption: str) -> str:
        n = len(caption)
        if n < 3:
            return ""

        # Create a list to store the minimum cost to make each character good
        dp = [float('inf')] * 26

        # Iterate through the caption from right to left
        for i in range(n-1, -1, -1):
            # Calculate the cost to make the current character good
            cost = 1
            if caption[i] == 'a':
                cost = 0
            elif caption[i] == 'z':
                cost = 1
            else:
                cost = 2

            # Update the minimum cost for the current character
            dp[caption[i] - ord('a')] = min(dp[caption[i] - ord('a')], cost)

            # Update the minimum cost for the next character
            if i < n-1:
                dp[caption[i+1] - ord('a')] = min(dp[caption[i+1] - ord('a')], cost + 1)

        # Create the result string
        result = ""
        for i in range(n):
            # If the current character is good, add it to the result string
            if dp[caption[i] - ord('a')] == 0:
                result += caption[i]
            # If the current character is not good, add the next character to the result string
            else:
                result += chr(ord('a') + (caption[i] - ord('a') + 1) % 26)

        return result

