class Solution:
    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        # Build the graph
        graph = [[] for _ in range(n)]
        for a, b, w in edges:
            graph[a].append((b, w))
            graph[b].append((a, w))

        # Find the connected components
        visited = [False] * n
        components = []
        for i in range(n):
            if not visited[i]:
                component = []
                stack = [i]
                while stack:
                    node = stack.pop()
                    if not visited[node]:
                        visited[node] = True
                        component.append(node)
                        for neighbor, weight in graph[node]:
                            stack.append(neighbor)
                components.append(component)

        # Calculate the maximum edge weight for each component
        max_weights = [0] * len(components)
        for i, component in enumerate(components):
            if len(component) < threshold:
                continue
            weights = [0] * len(component)
            for j in range(len(component)):
                for k in range(j + 1, len(component)):
                    weights[j] = max(weights[j], graph[component[j]][component[k]][1])
                    weights[k] = max(weights[k], graph[component[k]][component[j]][1])
            max_weights[i] = max(weights)

        # Find the minimum maximum edge weight
        min_max_weight = float('inf')
        for weight in max_weights:
            if weight > threshold:
