class Solution:
    def maxSum(self, nums: List[int], k: int, m: int) -> int:
        n = len(nums)
        if n < k * m:
            return -1

        dp = [[0] * (k + 1) for _ in range(n + 1)]
        prefix_sum = [0] * (n + 1)

        for i in range(1, n + 1):
            prefix_sum[i] = prefix_sum[i - 1] + nums[i - 1]

        for i in range(1, n + 1):
            for j in range(1, k + 1):
                if i < m * j:
                    dp[i][j] = dp[i - 1][j]
                else:
                    dp[i][j] = max(dp[i - 1][j], prefix_sum[i] - prefix_sum[i - m * j])

        return dp[n][k]
