    def maxSum(self, nums: List[int], k: int, m: int) -> int:
        n = len(nums)
        if n < k * m:
            return -1

        # Initialize the dp array to store the maximum sum of k subarrays of length m
        dp = [0] * (n + 1)

        # Calculate the sum of the first k*m elements
        for i in range(k * m):
            dp[i + 1] = dp[i] + nums[i]

        # Calculate the prefix sums of the dp array
        for i in range(1, n + 1):
            dp[i] = max(dp[i], dp[i - 1])

