    def maxSum(self, nums: List[int], k: int, m: int) -> int:
        n = len(nums)
        if n < k * m:
            return -1

        # Initialize variables to store the maximum sum and the current sum of the first k elements
        max_sum = float('-inf')
        current_sum = sum(nums[:k])

        # Iterate through the array starting from the kth element
        for i in range(k, n):
            # Update the current sum by adding the next element and subtracting the element that is k positions before the current element
            current_sum += nums[i] - nums[i - k]

            # If the current sum is greater than the maximum sum, update the maximum sum
            if current_sum > max_sum:
                max_sum = current_sum

        return max_sum

