class Solution:
    def minCost(self, n: int, cost: List[List[int]]) -> int:
        if n == 2:
            return min(cost[0][0], cost[1][0])

        dp = [[0, 0, 0] for _ in range(n)]
        dp[0] = cost[0]
        dp[1] = [min(dp[0][0] + cost[1][1], dp[0][1] + cost[1][0]), 
                  min(dp[0][0] + cost[1][2], dp[0][2] + cost[1][0]), 
                  min(dp[0][1] + cost[1][2], dp[0][2] + cost[1][1])]

        for i in range(2, n):
            dp[i][0] = min(dp[i-1][1] + cost[i][2], dp[i-1][2] + cost[i][1])
            dp[i][1] = min(dp[i-1][0] + cost[i][2], dp[i-1][2] + cost[i][0])
            dp[i][2] = min(dp[i-1][0] + cost[i][1], dp[i-1][1] + cost[i][0])

        return min(dp[-1])
