class Solution:
    def minCost(self, n: int, cost: List[List[int]]) -> int:
        if n == 0:
            return 0

        # Initialize the first row of dp
        dp = cost[0]

        # Iterate through the remaining houses
        for i in range(1, n):
            # Calculate the minimum cost for the current house
            dp[i] = min(dp[i-1][0] + cost[i][1], dp[i-1][1] + cost[i][0])
            dp[i] = min(dp[i], dp[i-1][2] + cost[i][0])
            dp[i] = min(dp[i], dp[i-1][2] + cost[i][1])

        # Return the minimum cost for the last house
        return min(dp[-1])
