class Solution:
    def distanceSum(self, m: int, n: int, k: int) -> int:
        MOD = 10**9 + 7

        # Calculate the number of ways to place k pieces on an m x n grid
        def count_ways(m, n, k):
            if k == 0:
                return 1
            if k > m * n:
                return 0
            return (count_ways(m, n, k - 1) * (m + n - k)) % MOD

        # Calculate the Manhattan distance between two cells (x_i, y_i) and (x_j, y_j)
        def manhattan_distance(x1, y1, x2, y2):
            return abs(x1 - x2) + abs(y1 - y2)

        # Calculate the total Manhattan distance across all valid arrangements
        total_distance = 0
        for i in range(m):
            for j in range(n):
                for x in range(m):
                    for y in range(n):
                        if (i, j) != (x, y):
                            total_distance = (total_distance + manhattan_distance(i, j, x, y)) % MOD

        return total_distance
