                            dp[i][j] = (dp[i][j] + dp[i - (x + y)][j - 1]) % MOD
        return sum(dp[i][k] for i in range(1, m * n + 1)) % MOD

class Solution:
    def distanceSum(self, m: int, n: int, k: int) -> int:
        MOD = 10**9 + 7
        dp = [[0] * (k + 1) for _ in range(m * n + 1)]
        for i in range(1, m * n + 1):
            dp[i][1] = i
        for i in range(2, k + 1):
            for j in range(1, m * n + 1):
                for x in range(1, m * n + 1):
                    if x != j:
                        dp[j][i] = (dp[j][i] + dp[x][i - 1] * (m * n - x)) % MOD
        return sum(dp[j][k] for j in range(1, m * n + 1)) % MOD

