class Solution:
    def maxFreeTime(self, eventTime: int, k: int, startTime: List[int], endTime: List[int]) -> int:
        # Sort the meetings by their start time
        meetings = sorted(zip(startTime, endTime))

        # Initialize variables to keep track of the current end time and the maximum free time
        current_end = 0
        max_free_time = 0

        # Iterate through the sorted meetings
        for start, end in meetings:
            # If the current meeting starts after the current end time, update the current end time
            if start > current_end:
                current_end = end
            # If we have used up all the available rescheduling slots, break the loop
            elif k == 0:
                break
            # Otherwise, decrement the available rescheduling slots
            else:
                k -= 1

        # Calculate the maximum free time by subtracting the current end time from the event time
        return eventTime - current_end

