class Solution:
    def sortMatrix(self, grid: List[List[int]]) -> List[List[int]]:
        n = len(grid)
        for i in range(n):
            for j in range(n):
                grid[i][j] = self.sortDiagonal(grid, i, j)
        return grid

    def sortDiagonal(self, grid, i, j):
        diagonal = []
        while i < n and j < n:
            diagonal.append(grid[i][j])
            i += 1
            j += 1
        diagonal.sort(reverse=True)
        i = 0
        j = 0
        while i < n and j < n:
            grid[i][j] = diagonal.pop()
            i += 1
            j += 1
        return grid[i][j]
