class Solution:
    def solveQueries(self, nums: List[int], queries: List[int]) -> List[int]:
        n = len(nums)
        ans = [-1] * len(queries)
        index_map = {}

        # Build the index map
        for i in range(n):
            if nums[i] not in index_map:
                index_map[nums[i]] = [i]
            else:
                index_map[nums[i]].append(i)

        # Process each query
        for i, q in enumerate(queries):
            if q in index_map:
                indices = index_map[q]
                if len(indices) == 1:
                    ans[i] = -1
                else:
                    # Find the closest index
                    left = bisect.bisect_left(indices, q)
                    right = bisect.bisect_right(indices, q)
                    if left == 0:
                        ans[i] = indices[1] - q
                    elif right == len(indices):
                        ans[i] = q - indices[-2]
                    else:
                        ans[i] = min(indices[right] - q, q - indices[left - 1])

        return ans
