        nums2.sort()
        ans = [0] * len(nums1)
        i, j = 0, len(nums2) - 1
        while i < len(nums1) and j >= 0 and k > 0:
            if nums1[i] < nums2[j]:
                ans[i] += nums2[j]
                j -= 1
                k -= 1
            else:
                i += 1
        while i < len(nums1) and k > 0:
            ans[i] += nums1[i]
            i += 1
            k -= 1
        while j >= 0 and k > 0:
            ans[j] += nums2[j]
            j -= 1
            k -= 1
        return ans

class Solution:
    def findMaxSum(self, nums1: List[int], nums2: List[int], k: int) -> List[int]:
        n = len(nums1)
        ans = [0] * n
        heap1 = []
        heap2 = []

        for i in range(n):
            heapq.heappush(heap1, -nums1[i])
            heapq.heappush(heap2, -nums2[i])

        for i in range(n):
            if nums1[i] < nums1[i - 1]:
                heapq.heappush(heap1, -nums1[i])
                heapq.heappop(heap1)
            if nums2[i] < nums2[i - 1]:
                heapq.heappush(heap2, -nums2[i])
                heapq.heappop(heap2)

            if len(heap1) > k:
                ans[i] = -heapq.heappop(heap1)
            if len(heap2) > k:
                ans[i] = -heapq.heappop(heap2)

        return ans

