        squares.sort(key=lambda x: x[1])
        areas = []
        for x, y, l in squares:
            area = l * l
            while areas and areas[-1][0] <= x:
                area -= areas.pop()[1]
            areas.append((x, area))
        return sum(a[1] for a in areas) / len(areas)

class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        squares.sort(key=lambda x: (x[0], x[1]))
        left = 0
        right = 0
        total_area = 0
        for x, y, l in squares:
            total_area += l * l
            while left < right and x + l <= squares[right][0]:
                total_area -= squares[right][2] * squares[right][2]
                right -= 1
            while left < right and x <= squares[left][0] + squares[left][2]:
                total_area += squares[left][2] * squares[left][2]
                left += 1
        return total_area / (right - left + 1)

class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        squares.sort(key=lambda x: x[1])
        total_area = 0
        for x, y, l in squares:
            total_area += l * l
        left, right = 0, 1
        while right < len(squares):
            x1, y1, l1 = squares[left]
            x2, y2, l2 = squares[right]
            if y1 + l1 <= y2:
                total_area -= l1 * l1
                left += 1
            else:
                total_area += l2 * l2
                right += 1
        return total_area / 2

class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        squares.sort(key=lambda x: x[1])
        total_area = 0
        for x, y, l in squares:
            total_area += l * l
        left = 0
        right = 0
        while right < len(squares):
            x, y, l = squares[right]
            while left < right and squares[left][1] + l <= y:
                left += 1
            total_area -= (squares[left][1] + l) * (squares[left][1] + l)
            right += 1
        return total_area / (2 * total_area)

