class Solution:
    def maxProduct(self, nums: List[int], k: int, limit: int) -> int:
        nums.sort()
        n = len(nums)
        dp = [[-1] * (k + 1) for _ in range(n + 1)]
        dp[0][0] = 1

        for i in range(1, n + 1):
            for j in range(k + 1):
                if j < nums[i - 1]:
                    dp[i][j] = dp[i - 1][j]
                else:
                    dp[i][j] = max(dp[i - 1][j], dp[i - 1][j - nums[i - 1]] * nums[i - 1])

        return dp[n][k] if dp[n][k] != -1 else -1
