class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        if k > self.get_permutation_count(n):
            return []

        return self.get_kth_permutation(n, k)

    def get_permutation_count(self, n):
        count = 1
        for i in range(1, n + 1):
            count *= i
        return count

    def get_kth_permutation(self, n, k):
        if n == 1:
            return [1]

        # Find the position of the first odd number
        odd_count = 0
        for i in range(1, n + 1):
            if i % 2 != 0:
                odd_count += 1

        # Calculate the position of the first odd number in the k-th permutation
        first_odd_position = (k - 1) // odd_count + 1

        # Remove the first odd number from the list
        odd_numbers = [i for i in range(1, n + 1) if i % 2 != 0]
        odd_numbers.remove(first_odd_position)

        # Get the k-th permutation of the remaining numbers
        remaining_permutation = self.get_kth_permutation(n - 1, (k - 1) % odd_count + 1)

        # Insert the first odd number at the correct position
        result = [first_odd_position]
        result.extend(remaining_permutation)

        return result
