class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        if k > math.factorial(n):
            return []

        def generate_permutations(n, k, result):
            if n == 1:
                result.append([1])
                return
            for i in range(1, n + 1):
                if i % 2 == 1:
                    if k % 2 == 1:
                        generate_permutations(n - 1, k // 2, result)
                    else:
                        generate_permutations(n - 1, k // 2 + 1, result)
                else:
                    if k % 2 == 0:
                        generate_permutations(n - 1, k // 2, result)
                    else:
                        generate_permutations(n - 1, k // 2 + 1, result)
            result.sort()

        result = []
        generate_permutations(n, k, result)
        return result[k - 1]
