class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        if k > factorial(n):
            return []

        def generate_permutation(n, k, start, end, result):
            if start == end:
                result.append(list(range(1, n+1)))
                return

            for i in range(start, end):
                result.append(list(range(1, n+1)))
                result[-1][start], result[-1][i] = result[-1][i], result[-1][start]
                generate_permutation(n, k, start+1, end, result)
                result.pop()

        result = []
        generate_permutation(n, k, 0, n, result)
        return result[k-1]
