class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        if k > factorial(n):
            return []

        def generate_permutations(n, k):
            if n == 1:
                return [1]

            # Find the index of the first odd number
            odd_index = next((i for i in range(n) if (i + 1) % 2 != 0), None)

            # Generate the first half of the permutation
            first_half = generate_permutations(odd_index, k // 2)

            # Generate the second half of the permutation
            second_half = generate_permutations(n - odd_index - 1, k - k // 2)

            # Combine the two halves
            return [first_half[i] + second_half[i] for i in range(n)]

        return generate_permutations(n, k)
