class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        if k > math.factorial(n):
            return []

        def generate_permutations(n, k):
            if n == 1:
                return [1]

            # Find the position of the first odd number
            odd_count = sum(1 for i in range(1, n + 1) if i % 2 != 0)
            if k <= odd_count:
                return [i for i in range(1, n + 1) if i % 2 != 0] + generate_permutations(n - 1, k)
            else:
                return [i for i in range(1, n + 1) if i % 2 == 0] + generate_permutations(n - 1, k - odd_count)

        return generate_permutations(n, k)

