class Solution:
    def maxSubarrays(self, n: int, conflictingPairs: List[List[int]]) -> int:
        # Sort the conflicting pairs by their first element
        conflictingPairs.sort()

        # Create a dictionary to store the end index of each number
        end_index = {}
        for a, b in conflictingPairs:
            if a in end_index:
                end_index[a] = max(end_index[a], b)
            else:
                end_index[a] = b

        # Initialize variables to keep track of the maximum number of subarrays
        max_subarrays = 0
        current_subarrays = 0

        # Iterate through the numbers from 1 to n
        for i in range(1, n + 1):
            # If the current number is not in the conflicting pairs, it can be included in the current subarray
            if i not in end_index:
                current_subarrays += 1
            else:
                # If the current number is in the conflicting pairs, it cannot be included in the current subarray
                current_subarrays = 0
            # Update the maximum number of subarrays
            max_subarrays = max(max_subarrays, current_subarrays)

        return max_subarrays
