    def maxSubarrays(self, n: int, conflictingPairs: List[List[int]]) -> int:
        # Create a set to store the conflicting pairs
        conflicting_pairs_set = set(conflictingPairs)

        # Create a dictionary to store the maximum number of subarrays that can be formed
        max_subarrays = [0] * (n + 1)

        # Iterate through the array from 1 to n
        for i in range(1, n + 1):
            # Check if the current number is in the conflicting pairs set
            if (i, i + 1) in conflicting_pairs_set or (i - 1, i) in conflicting_pairs_set:
                # If it is, set the maximum number of subarrays to 0
                max_subarrays[i] = 0
            else:
                # Otherwise, set the maximum number of subarrays to the maximum of the previous two values
                max_subarrays[i] = max(max_subarrays[i - 1], max_subarrays[i - 2]) + 1

        # Return the maximum number of subarrays that can be formed after removing one element from conflictingPairs
        return max(max_subarrays)
