class Solution:
    def numOfUnplacedFruits(self, fruits: List[int], baskets: List[int]) -> int:
        # Initialize variables
        left, right = 0, 0
        fruit_count = {}
        max_fruits = 0
        unplaced_fruits = 0

        # Count the frequency of each fruit
        for fruit in fruits:
            if fruit in fruit_count:
                fruit_count[fruit] += 1
            else:
                fruit_count[fruit] = 1

        # Calculate the maximum number of fruits that can be placed in the baskets
        for count in fruit_count.values():
            max_fruits = max(max_fruits, count)

        # Use a sliding window to find the maximum number of fruits that can be placed in the baskets
        while right < len(fruits):
            # Add the current fruit to the window
            if fruits[right] in fruit_count:
                fruit_count[fruits[right]] += 1
            else:
                fruit_count[fruits[right]] = 1

            # Calculate the number of fruits that cannot be placed in the window
            unplaced_fruits += max_fruits - min(fruit_count.values())

            # Move the left pointer to the right
            while unplaced_fruits > baskets[right]:
                if fruits[left] in fruit_count:
                    fruit_count[fruits[left]] -= 1
                    if fruit_count[fruits[left]] == 0:
                        del fruit_count[fruits[left]]
                unplaced_fruits -= max_fruits - min(fruit_count.values())
                left += 1

            right += 1

        return unplaced_fruits

