class Solution:
    def longestPalindrome(self, s: str, t: str) -> int:
        # Create a dictionary to store the frequency of each character in both strings
        s_freq = {}
        t_freq = {}
        for char in s:
            s_freq[char] = s_freq.get(char, 0) + 1
        for char in t:
            t_freq[char] = t_freq.get(char, 0) + 1

        # Initialize the result variable
        result = 0

        # Iterate through the characters in the dictionary
        for char, freq in s_freq.items():
            # If the frequency is even, add it to the result
            if freq % 2 == 0:
                result += freq
            # If the frequency is odd, add the frequency minus one to the result
            else:
                result += freq - 1

        # Iterate through the characters in the dictionary
        for char, freq in t_freq.items():
            # If the character is not in the dictionary, add it to the result
            if char not in s_freq:
                result += freq

        # If the result is even, return it
        if result % 2 == 0:
            return result
        # Otherwise, return result + 1
        else:
            return result + 1
