class Solution:
    def maxContainers(self, n: int, w: int, maxWeight: int) -> int:
        # Initialize the maximum number of containers to 0
        max_containers = 0

        # Iterate over all possible combinations of containers
        for i in range(1, n + 1):
            for j in range(1, n + 1):
                for k in range(1, n + 1):
                    # Calculate the total weight of the current combination
                    total_weight = i * j * k

                    # Check if the total weight is within the maximum weight capacity
                    if total_weight <= maxWeight:
                        # Update the maximum number of containers if the current combination is valid
                        max_containers = max(max_containers, i + j + k)

        # Return the maximum number of containers that can be loaded onto the ship
        return max_containers
