def can_reach_end(N, M, A, B, pairs):
    # Create a set of bad squares
    bad_squares = set()
    for L, R in pairs:
        for i in range(L, R + 1):
            bad_squares.add(i)

    # Check if square 1 is bad
    if 1 in bad_squares:
        return "No"

    # Check if square N is bad
    if N in bad_squares:
        return "No"

    # Check if we can reach square N
    current = 1
    while current < N:
        if current + A > N:
            return "No"
        if current + A in bad_squares:
            return "No"
        current += A
        if current > N:
            return "No"
        if current + B < N:
            return "No"
        if current + B in bad_squares:
            return "No"
        current += B

    return "Yes"

