def can_sort_by_one_swap(A):
    # Check if the sequence is already sorted
    if A == sorted(A):
        return "No"

    # Find the index of the first element that is greater than the next element
    for i in range(len(A) - 1):
        if A[i] > A[i + 1]:
            # Check if swapping this element with the next element will sort the sequence
            if A[i + 1] > A[i + 2]:
                return "No"
            else:
                return "Yes"

    return "No"

