        return "No"

    # Check if all black cells form a rectangle
    for i in range(H):
        for j in range(W):
            if grid[i][j] == '#':
                for a in range(i, H):
                    for b in range(j, W):
                        for c in range(i, a + 1):
                            for d in range(j, b + 1):
