def f(L, R, A):
    blackboard = A[L-1:R]
    operations = 0
    while blackboard:
        l, r = 0, len(blackboard) - 1
        while l < r:
            mid = (l + r) // 2
            if blackboard[mid] in blackboard[l:r+1]:
                l = mid + 1
            else:
                r = mid
        for i in range(l, r+1):
            blackboard.remove(blackboard[i])
        operations += 1
    return operations

