    grid = [[0] * W for _ in range(N)]

    # Place the blocks on the grid
    for x, y in blocks:
        grid[N - y - 1][x - 1] = 1

    # Process the times
    for t in times:
        # Check if the bottom row is filled with blocks
        if all(grid[N - 1][i] == 1 for i in range(W)):
            # Remove the bottom row
            grid.pop()

        # Move the blocks
        for i in range(N - 1, 0, -1):
            for j in range(W):
                if grid[i][j] == 1 and (i == N - 1 or grid[i - 1][j] == 1):
                    grid[i][j] = 0
                elif grid[i][j] == 1 and grid[i - 1][j] == 0:
                    grid[i][j] = 1
                    grid[i - 1][j] = 0

    # Check if block A exists at time T + 0.5
    for i in range(Q):
        T, A = times[i]
        if T + 0.5 < N:
            if grid[T + 0.5 - 1][A - 1] == 1:
                print("Yes")
            else:
                print("No")
        else:
            print("No")

