def main():
    input = sys.stdin.read
    data = input().split()
    N = int(data[0])
    dice = []
    for i in range(N):
        K = int(data[2*i+1])
        A = list(map(int, data[2*i+2:2*i+2+K]))
        dice.append((K, A))

    max_prob = 0
    for i in range(N):
        for j in range(i+1, N):
            prob = 1
            for k in range(dice[i][0]):
                prob *= dice[i][1][k] / dice[i][0]
            for k in range(dice[j][0]):
                prob *= dice[j][1][k] / dice[j][0]
            max_prob = max(max_prob, prob)

    print(max_prob)

