def longest_increasing_subsequence(A, X):
    n = len(A)
    dp = [1] * n
    for i in range(1, n):
        for j in range(i):
            if A[i] > A[j] and X[i] >= A[j]:
                dp[i] = max(dp[i], dp[j] + 1)
    return max(dp)

def solve_queries(N, Q, A, R, X):
    queries = []
    for i in range(Q):
        queries.append((R[i], X[i]))
    queries.sort()
    result = []
    for R, X in queries:
        result.append(longest_increasing_subsequence(A[:R], X))
    return result

