        return s == s[::-1]

    def dfs(start, end, path):
        if start == end:
            return len(path)
        if start not in graph:
            return -1
        min_length = float('inf')
        for neighbor in graph[start]:
            if neighbor not in path:
                path.append(neighbor)
                length = dfs(neighbor, end, path)
                if length != -1:
                    min_length = min(min_length, length + 1)
                path.pop()
        return min_length

    for i in range(N):
        for j in range(N):
            if is_palindrome(''.join([edges[i * N + k] for k in range(i, j + 1)])):
                min_length = dfs(i, j, [i])
                if min_length != -1:
                    print(min_length, end=' ')
                else:
                    print(-1, end=' ')
            else:
                print(-1, end=' ')
        print()

