def shortest_palindrome_path(n, edges):
    graph = [[] for _ in range(n + 1)]
    for i in range(n):
        for j in range(n):
            if edges[i * n + j] != '-':
                graph[i + 1].append(j + 1)

    def dfs(node, path, visited):
        if node == n:
            if is_palindrome(path):
                return len(path) - 1
            return float('inf')

        visited.add(node)
        min_length = float('inf')
        for neighbor in graph[node]:
            if neighbor not in visited:
                min_length = min(min_length, dfs(neighbor, path + edges[node * n + neighbor - 1], visited))
        visited.remove(node)
        return min_length

    min_lengths = [-1] * (n + 1)
    for i in range(1, n + 1):
        min_lengths[i] = dfs(i, edges[i * n - 1], set())

    return min_lengths

