import sys
from collections import defaultdict

def read_input():
    H, W = map(int, sys.stdin.readline().split())
    F = [list(map(int, sys.stdin.readline().split())) for _ in range(H)]
    Q = int(sys.stdin.readline())
    queries = []
    for _ in range(Q):
        A, B, Y, C, D, Z = map(int, sys.stdin.readline().split())
        queries.append((A, B, Y, C, D, Z))
    return H, W, F, Q, queries

def min_stairs_moves(H, W, F, Q, queries):
    # Build adjacency list for adjacent blocks
    adj = defaultdict(list)
    for i in range(H):
        for j in range(W):
            for di, dj in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                ni, nj = i + di, j + dj
                if 0 <= ni < H and 0 <= nj < W:
                    adj[(i, j)].append((ni, nj))
                    adj[(ni, nj)].append((i, j))

    # Initialize memoization table
    memo = defaultdict(lambda: float('inf'))
    memo[(0, 0)] = 0

    def dfs(i, j, X):
        if (i, j, X) in memo:
            return memo[(i, j, X)]
        if X == 0:
            return 0
        min_moves = float('inf')
        for ni, nj in adj[(i, j)]:
            if ni == i and nj == j:
                min_moves = min(min_moves, dfs(ni, nj, X - 1))
            else:
                min_moves = min(min_moves, dfs(ni, nj, X) + 1)
        memo[(i, j, X)] = min_moves
        return min_moves

    results = []
    for A, B, Y, C, D, Z in queries:
        results.append(dfs(A - 1, B - 1, Y - 1))
    return results

H, W, F, Q, queries = read_input()
