def min_stairs_moves(H, W, F, Q, queries):
    # Create a dictionary to store the number of stairs needed to move from one floor to another
    stairs_needed = {}

    # Calculate the number of stairs needed to move from one floor to another
    for i in range(H):
        for j in range(W):
            for k in range(1, F[i][j] + 1):
                if (i, j, k) not in stairs_needed:
                    stairs_needed[(i, j, k)] = 1
                if (i, j, k - 1) in stairs_needed:
                    stairs_needed[(i, j, k)] = min(stairs_needed[(i, j, k)], stairs_needed[(i, j, k - 1)] + 1)

    # Process each query
    for i in range(Q):
        A, B, Y, C, D, Z = queries[i]
        if (A, B) == (C, D):
            print(0)
            continue
        if (A, B) == (C, D - 1):
            print(stairs_needed[(C, D, Z)])
            continue
        if (A, B) == (C, D + 1):
            print(stairs_needed[(C, D, Z)])
            continue
        if (A, B) == (C - 1, D):
            print(stairs_needed[(C, D, Z)])
            continue
        if (A, B) == (C + 1, D):
            print(stairs_needed[(C, D, Z)])
            continue
        if (A, B) == (C - 1, D - 1):
            print(stairs_needed[(C, D, Z)])
