def min_stairs_moves(H, W, F, Q, queries):
    # Build a graph where each node represents a building and each edge represents a possible move
    graph = [[] for _ in range(H * W)]
    for i in range(H):
        for j in range(W):
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = i + dx, j + dy
                if 0 <= nx < H and 0 <= ny < W:
                    if F[i][j] > F[nx][ny]:
                        graph[i * W + j].append(nx * W + ny)
                    elif F[i][j] < F[nx][ny]:
                        graph[nx * W + ny].append(i * W + j)

    # Run Dijkstra's algorithm for each query
    results = []
    for A, B, Y, C, D, Z in queries:
        start = A * W + B
        end = C * W + D
        dist = [float('inf')] * (H * W)
        dist[start] = 0
        pq = [(0, start)]
        while pq:
            d, u = heapq.heappop(pq)
            if d > dist[u]:
                continue
            if u == end:
                results.append(d)
                break
            for v in graph[u]:
                if dist[v] > d + 1:
                    dist[v] = d + 1
                    heapq.heappush(pq, (dist[v], v))
    return results

