def min_stairs_moves(H, W, F, Q, queries):
    # Build a graph where each node represents a building and each edge represents a possible move
    graph = [[] for _ in range(H * W)]
    for i in range(H):
        for j in range(W):
            for di, dj in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                ni, nj = i + di, j + dj
                if 0 <= ni < H and 0 <= nj < W:
                    graph[i * W + j].append(ni * W + nj)

    # Initialize the distance array with infinity
    dist = [[float('inf')] * W for _ in range(H)]

    # Start from the building in block (A_i, B_i) and set its distance to 0
    start = (A_i - 1) * W + (B_i - 1)
    dist[start][B_i - 1] = 0

    # Use Dijkstra's algorithm to find the shortest path to each building
    for _ in range(H * W):
        for i in range(H * W):
            for j in graph[i]:
                if dist[i][j] > dist[i][B_i - 1] + 1:
                    dist[j][B_i - 1] = dist[i][j]

    # Process each query
    results = []
    for A_i, B_i, Y_i, C_i, D_i, Z_i in queries:
        start = (A_i - 1) * W + (B_i - 1)
        end = (C_i - 1) * W + (D_i - 1)
        if dist[start][end] == float('inf'):
            results.append(-1)
        else:
            results.append(dist[start][end])

    return results

