def solve(N, M, X, edges):
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)

    visited = [False] * (N + 1)
    stack = [1]
    path = []
    while stack:
        u = stack.pop()
        if not visited[u]:
            visited[u] = True
            path.append(u)
            for v in graph[u]:
                stack.append(v)

    reverse_edges = []
    for u, v in edges:
        if u in path and v in path:
            reverse_edges.append((u, v))

    cost = 0
    while path:
        u = path.pop()
        if u == N:
            break
        for v in graph[u]:
            if (u, v) in reverse_edges:
                cost += X
            else:
                cost += 1
            path.append(v)

    return cost

