import sys

def xor_path(graph, start, end):
    def dfs(node, path):
        if node == end:
            return path
        for neighbor in graph[node]:
            if neighbor not in path:
                result = dfs(neighbor, path + [neighbor])
                if result:
                    return result
        return None

    def find_min_xor(graph, start, end):
        path = dfs(start, [start])
        if not path:
            return 0
        xor_sum = 0
        for i in range(1, len(path)):
            xor_sum ^= graph[path[i-1]][path[i]]
        return xor_sum

