def xor_path(graph, start, end):
    n = len(graph)
    visited = [False] * n
    path = []
    stack = [start]

    while stack:
        node = stack.pop()
        if not visited[node]:
            visited[node] = True
            path.append(node)
            for neighbor in graph[node]:
                stack.append(neighbor)

    xor_result = 0
    for i in range(len(path) - 1):
        xor_result ^= graph[path[i]][path[i + 1]]

    return xor_result

