import sys
from collections import defaultdict

def read_input():
    N, K = map(int, sys.stdin.readline().split())
    edges = defaultdict(list)
    for _ in range(N - 1):
        u, v = map(int, sys.stdin.readline().split())
        edges[u].append(v)
        edges[v].append(u)
    return N, K, edges

def is_valid_decomposition(N, K, edges):
    # Create a list to store the path for each vertex
    path = [0] * (N * K)

    # Start from vertex 1
    current_vertex = 1
    path[0] = current_vertex
    current_position = 1

    # Traverse the tree and fill the path
    while current_position < N * K:
        for neighbor in edges[current_vertex]:
            if neighbor != path[current_position - 1]:
                path[current_position] = neighbor
                current_position += 1
                current_vertex = neighbor
                break
        else:
            return False

    # Check if the path is valid
    for i in range(N - 1):
        if path[i] == path[i + 1]:
            return False

    return True

def main():
    N, K, edges = read_input()
    if is_valid_decomposition(N, K, edges):
        print("Yes")
    else:
        print("No")

